mapper.update:
UPDATE agencies


        name = #{update.name},


        abbr_code = #{update.abbrCode},


        region = #{update.region},


        address = #{update.address},


        status = #{update.status},


        type = #{update.type},


        parent = #{update.parent},


        create_dt = #{update.createDt},


        update_dt = #{update.updateDt},


        create_by = #{update.createBy},


        update_by = #{update.updateBy},





        AND id = #{condition.id}


        AND name = #{condition.name}


        AND abbr_code = #{condition.abbrCode}


        AND region = #{condition.region}


        AND address = #{condition.address}


        AND status = #{condition.status}


        AND type = #{condition.type}


        AND parent = #{condition.parent}


        AND create_dt = #{condition.createDt}


        AND update_dt = #{condition.updateDt}


        AND create_by = #{condition.createBy}


        AND update_by = #{condition.updateBy}

UPDATE agencies
name = #{name}, abbr_code = #{abbrCode}, region = #{region}, address = #{address}, status = #{status}, type = #{type}, parent = #{parent}, create_dt = #{createDt}, update_dt = #{updateDt}, create_by = #{createBy}, update_by = #{updateBy},
WHERE id = #{id}

mapper.select:
SELECT



