mapper.update:
UPDATE order_line


        fee = #{update.fee},


        user_id = #{update.userId},


        agency = #{update.agency},


        from_station = #{update.fromStation},


        to_station = #{update.toStation},


        power_bank = #{update.powerBank},


        fee_type = #{update.feeType},


        order_id = #{update.orderId},


        create_dt = #{update.createDt},


        update_dt = #{update.updateDt},


        create_by = #{update.createBy},


        update_by = #{update.updateBy},


        start_dt = #{update.startDt},


        end_dt = #{update.endDt},





    AND id = #{condition.id}


    AND fee = #{condition.fee}


    AND user_id = #{condition.userId}


    AND agency = #{condition.agency}


    AND from_station = #{condition.fromStation}


    AND to_station = #{condition.toStation}


    AND power_bank = #{condition.powerBank}


    AND fee_type = #{condition.feeType}


    AND order_id = #{condition.orderId}


    AND create_dt = #{condition.createDt}


    AND update_dt = #{condition.updateDt}


    AND create_by = #{condition.createBy}


    AND update_by = #{condition.updateBy}


    AND start_dt = #{condition.startDt}


    AND end_dt = #{condition.endDt}

UPDATE order_line
fee = #{fee}, user_id = #{userId}, agency = #{agency}, from_station = #{fromStation}, to_station = #{toStation}, power_bank = #{powerBank}, fee_type = #{feeType}, order_id = #{orderId}, create_dt = #{createDt}, update_dt = #{updateDt}, create_by = #{createBy}, update_by = #{updateBy}, start_dt = #{startDt}, end_dt = #{endDt},
WHERE id = #{id}

