mapper.update:
UPDATE orders


        surplus_fee = #{update.surplusFee},


        total_fee = #{update.totalFee},


        order_owner = #{update.orderOwner},


        agency = #{update.agency},


        type = #{update.type},


        status = #{update.status},


        create_dt = #{update.createDt},


        update_dt = #{update.updateDt},


        create_by = #{update.createBy},


        update_by = #{update.updateBy},


        order_no = #{update.orderNo},


        parent_order = #{update.parentOrder},


        parent_order_list = #{update.parentOrderList},


        trade_type = #{update.tradeType},


        trade_identify = #{update.tradeIdentify},


        editor = #{update.editor},


        des = #{update.des},


        editor_name = #{update.editorName},


        user_roles = #{update.userRoles},





    AND id = #{condition.id}


    AND surplus_fee = #{condition.surplusFee}


    AND total_fee = #{condition.totalFee}


    AND order_owner = #{condition.orderOwner}


    AND agency = #{condition.agency}


    AND type = #{condition.type}


    AND status = #{condition.status}


    AND create_dt = #{condition.createDt}


    AND update_dt = #{condition.updateDt}


    AND create_by = #{condition.createBy}


    AND update_by = #{condition.updateBy}


    AND order_no = #{condition.orderNo}


    AND parent_order = #{condition.parentOrder}


    AND parent_order_list = #{condition.parentOrderList}


    AND trade_type = #{condition.tradeType}


    AND trade_identify = #{condition.tradeIdentify}


    AND editor = #{condition.editor}


    AND des = #{condition.des}


    AND editor_name = #{condition.editorName}


    AND user_roles = #{condition.userRoles}

UPDATE orders
surplus_fee = #{surplusFee}, total_fee = #{totalFee}, order_owner = #{orderOwner}, agency = #{agency}, type = #{type}, status = #{status}, create_dt = #{createDt}, update_dt = #{updateDt}, create_by = #{createBy}, update_by = #{updateBy}, order_no = #{orderNo}, parent_order = #{parentOrder}, parent_order_list = #{parentOrderList}, trade_type = #{tradeType}, trade_identify = #{tradeIdentify}, editor = #{editor}, des = #{des}, editor_name = #{editorName}, user_roles = #{userRoles},
WHERE id = #{id}

