UPDATE power_platform_info
appId = #{appId}, secret = #{secret}, type = #{type}, adminUser = #{adminUser}, token = #{token}, aesKey = #{aesKey}, uniqueKey = #{uniqueKey}, note = #{note}, agencyId = #{agencyId},
WHERE id = #{id}

