            , note


            , agencyId







        FROM power_platform_info




        AND   id  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   id  ${condition.id.op}
        #{listItem}





        AND   appId  ${condition.appId.op} #{condition.appId.data}


        AND appId like CONCAT('%', #{condition.appId.data} , '%')


        AND appId like CONCAT(#{condition.appId.data} , '%')


        ${condition.groupOp}   appId  ${condition.appId.op}
        #{listItem}





        AND   secret  ${condition.secret.op} #{condition.secret.data}


        AND secret like CONCAT('%', #{condition.secret.data} , '%')


        AND secret like CONCAT(#{condition.secret.data} , '%')


        ${condition.groupOp}   secret  ${condition.secret.op}
        #{listItem}





        AND   type  ${condition.type.op} #{condition.type.data}


        AND type like CONCAT('%', #{condition.type.data} , '%')


        AND type like CONCAT(#{condition.type.data} , '%')


        ${condition.groupOp}   type  ${condition.type.op}
        #{listItem}





        AND   adminUser  ${condition.adminUser.op} #{condition.adminUser.data}


        AND adminUser like CONCAT('%', #{condition.adminUser.data} , '%')


        AND adminUser like CONCAT(#{condition.adminUser.data} , '%')


        ${condition.groupOp}   adminUser  ${condition.adminUser.op}
        #{listItem}





        AND   token  ${condition.token.op} #{condition.token.data}


        AND token like CONCAT('%', #{condition.token.data} , '%')


        AND token like CONCAT(#{condition.token.data} , '%')


        ${condition.groupOp}   token  ${condition.token.op}
        #{listItem}





        AND   aesKey  ${condition.aesKey.op} #{condition.aesKey.data}


        AND aesKey like CONCAT('%', #{condition.aesKey.data} , '%')


        AND aesKey like CONCAT(#{condition.aesKey.data} , '%')


        ${condition.groupOp}   aesKey  ${condition.aesKey.op}
        #{listItem}





        AND   uniqueKey  ${condition.uniqueKey.op} #{condition.uniqueKey.data}


        AND uniqueKey like CONCAT('%', #{condition.uniqueKey.data} , '%')


        AND uniqueKey like CONCAT(#{condition.uniqueKey.data} , '%')


        ${condition.groupOp}   uniqueKey  ${condition.uniqueKey.op}
        #{listItem}





        AND   note  ${condition.note.op} #{condition.note.data}


        AND note like CONCAT('%', #{condition.note.data} , '%')


        AND note like CONCAT(#{condition.note.data} , '%')


        ${condition.groupOp}   note  ${condition.note.op}
        #{listItem}





        AND   agencyId  ${condition.agencyId.op} #{condition.agencyId.data}


        AND agencyId like CONCAT('%', #{condition.agencyId.data} , '%')


        AND agencyId like CONCAT(#{condition.agencyId.data} , '%')


        ${condition.groupOp}   agencyId  ${condition.agencyId.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

