mapper.update:
UPDATE power_user_account


        userId = #{update.userId},


        agencyId = #{update.agencyId},


        create_dt = #{update.createDt},


        update_dt = #{update.updateDt},


        create_by = #{update.createBy},


        update_by = #{update.updateBy},





    AND id = #{condition.id}


    AND userId = #{condition.userId}


    AND agencyId = #{condition.agencyId}


    AND create_dt = #{condition.createDt}


    AND update_dt = #{condition.updateDt}


    AND create_by = #{condition.createBy}


    AND update_by = #{condition.updateBy}

UPDATE power_user_account
userId = #{userId}, agencyId = #{agencyId}, create_dt = #{createDt}, update_dt = #{updateDt}, create_by = #{createBy}, update_by = #{updateBy},
WHERE id = #{id}

