mapper.update:
UPDATE power_user


        account_no = #{update.accountNo},


        phone = #{update.phone},


        email = #{update.email},


        username = #{update.username},


        password = #{update.password},


        create_dt = #{update.createDt},


        update_dt = #{update.updateDt},





    AND id = #{condition.id}


    AND account_no = #{condition.accountNo}


    AND phone = #{condition.phone}


    AND email = #{condition.email}


    AND username = #{condition.username}


    AND password = #{condition.password}


    AND create_dt = #{condition.createDt}


    AND update_dt = #{condition.updateDt}

UPDATE power_user
account_no = #{accountNo}, phone = #{phone}, email = #{email}, username = #{username}, password = #{password}, create_dt = #{createDt}, update_dt = #{updateDt},
WHERE id = #{id}

mapper.select:
SELECT



            , id


            , account_no


            , phone


            , email


            , username


            , password


            , create_dt


            , update_dt







        FROM power_user




        AND   id  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   id  ${condition.id.op}
        #{listItem}





        AND   account_no  ${condition.accountNo.op} #{condition.accountNo.data}


        AND account_no like CONCAT('%', #{condition.accountNo.data} , '%')


        AND account_no like CONCAT(#{condition.accountNo.data} , '%')


        ${condition.groupOp}   account_no  ${condition.accountNo.op}
        #{listItem}





        AND   phone  ${condition.phone.op} #{condition.phone.data}


        AND phone like CONCAT('%', #{condition.phone.data} , '%')


        AND phone like CONCAT(#{condition.phone.data} , '%')


        ${condition.groupOp}   phone  ${condition.phone.op}
        #{listItem}





        AND   email  ${condition.email.op} #{condition.email.data}


        AND email like CONCAT('%', #{condition.email.data} , '%')


        AND email like CONCAT(#{condition.email.data} , '%')


        ${condition.groupOp}   email  ${condition.email.op}
        #{listItem}





        AND   username  ${condition.username.op} #{condition.username.data}


        AND username like CONCAT('%', #{condition.username.data} , '%')


        AND username like CONCAT(#{condition.username.data} , '%')


        ${condition.groupOp}   username  ${condition.username.op}
        #{listItem}





        AND   password  ${condition.password.op} #{condition.password.data}


        AND password like CONCAT('%', #{condition.password.data} , '%')


        AND password like CONCAT(#{condition.password.data} , '%')


        ${condition.groupOp}   password  ${condition.password.op}
        #{listItem}





        AND   create_dt  ${condition.createDt.op} #{condition.createDt.data}


        AND create_dt like CONCAT('%', #{condition.createDt.data} , '%')


        AND create_dt like CONCAT(#{condition.createDt.data} , '%')


        ${condition.groupOp}   create_dt  ${condition.createDt.op}
        #{listItem}





        AND   update_dt  ${condition.updateDt.op} #{condition.updateDt.data}


        AND update_dt like CONCAT('%', #{condition.updateDt.data} , '%')


        AND update_dt like CONCAT(#{condition.updateDt.data} , '%')


        ${condition.groupOp}   update_dt  ${condition.updateDt.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

