UPDATE power_user_expand
nickname = #{nickname}, headimgurl = #{headimgurl}, sex = #{sex}, city = #{city}, country = #{country}, province = #{province}, weixin = #{weixin}, agencyId = #{agencyId}, userId = #{userId},
WHERE id = #{id}

