                #{listItem}






            AND sex ${condition.sex.op} #{condition.sex.data}


            AND sex like CONCAT('%', #{condition.sex.data} , '%')


            AND sex like CONCAT(#{condition.sex.data} , '%')


            ${condition.groupOp} sex ${condition.sex.op}

                #{listItem}






            AND city ${condition.city.op} #{condition.city.data}


