            AND city like CONCAT(#{condition.city.data} , '%')


            ${condition.groupOp} city ${condition.city.op}

                #{listItem}






            AND country ${condition.country.op} #{condition.country.data}


            AND country like CONCAT('%', #{condition.country.data} , '%')


            AND country like CONCAT(#{condition.country.data} , '%')


            ${condition.groupOp} country ${condition.country.op}

                #{listItem}






