mapper.update:
UPDATE power_user_platform


        platformId = #{update.platformId},


        openId = #{update.openId},


        unionId = #{update.unionId},


        createTime = #{update.createTime},


        userId = #{update.userId},


        status = #{update.status},





    AND id = #{condition.id}


    AND platformId = #{condition.platformId}


    AND openId = #{condition.openId}


    AND unionId = #{condition.unionId}


    AND createTime = #{condition.createTime}


    AND userId = #{condition.userId}


    AND status = #{condition.status}

UPDATE power_user_platform
platformId = #{platformId}, openId = #{openId}, unionId = #{unionId}, createTime = #{createTime}, userId = #{userId}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT



            , id


            , platformId


            , openId


            , unionId


            , createTime


            , userId


            , status







        FROM power_user_platform




        AND   id  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   id  ${condition.id.op}
        #{listItem}





        AND   platformId  ${condition.platformId.op} #{condition.platformId.data}


        AND platformId like CONCAT('%', #{condition.platformId.data} , '%')


        AND platformId like CONCAT(#{condition.platformId.data} , '%')


        ${condition.groupOp}   platformId  ${condition.platformId.op}
        #{listItem}





        AND   openId  ${condition.openId.op} #{condition.openId.data}


        AND openId like CONCAT('%', #{condition.openId.data} , '%')


        AND openId like CONCAT(#{condition.openId.data} , '%')


        ${condition.groupOp}   openId  ${condition.openId.op}
        #{listItem}





        AND   unionId  ${condition.unionId.op} #{condition.unionId.data}


        AND unionId like CONCAT('%', #{condition.unionId.data} , '%')


        AND unionId like CONCAT(#{condition.unionId.data} , '%')


        ${condition.groupOp}   unionId  ${condition.unionId.op}
        #{listItem}





        AND   createTime  ${condition.createTime.op} #{condition.createTime.data}


        AND createTime like CONCAT('%', #{condition.createTime.data} , '%')


        AND createTime like CONCAT(#{condition.createTime.data} , '%')


        ${condition.groupOp}   createTime  ${condition.createTime.op}
        #{listItem}





        AND   userId  ${condition.userId.op} #{condition.userId.data}


        AND userId like CONCAT('%', #{condition.userId.data} , '%')


        AND userId like CONCAT(#{condition.userId.data} , '%')


        ${condition.groupOp}   userId  ${condition.userId.op}
        #{listItem}





        AND   status  ${condition.status.op} #{condition.status.data}


        AND status like CONCAT('%', #{condition.status.data} , '%')


        AND status like CONCAT(#{condition.status.data} , '%')


        ${condition.groupOp}   status  ${condition.status.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

