SELECT



            , id


            , msg_id


            , msg_name


            , msg_title


            , msg_type


            , content_model


            , uniquekey







        FROM power_wx_msg




        AND   id  ${condition.id.op} #{condition.id.data}


        AND id like CONCAT('%', #{condition.id.data} , '%')


        AND id like CONCAT(#{condition.id.data} , '%')


        ${condition.groupOp}   id  ${condition.id.op}
        #{listItem}





        AND   msg_id  ${condition.msgId.op} #{condition.msgId.data}


        AND msg_id like CONCAT('%', #{condition.msgId.data} , '%')


        AND msg_id like CONCAT(#{condition.msgId.data} , '%')


        ${condition.groupOp}   msg_id  ${condition.msgId.op}
        #{listItem}





        AND   msg_name  ${condition.msgName.op} #{condition.msgName.data}


        AND msg_name like CONCAT('%', #{condition.msgName.data} , '%')


        AND msg_name like CONCAT(#{condition.msgName.data} , '%')


        ${condition.groupOp}   msg_name  ${condition.msgName.op}
        #{listItem}





        AND   msg_title  ${condition.msgTitle.op} #{condition.msgTitle.data}


        AND msg_title like CONCAT('%', #{condition.msgTitle.data} , '%')


        AND msg_title like CONCAT(#{condition.msgTitle.data} , '%')


        ${condition.groupOp}   msg_title  ${condition.msgTitle.op}
        #{listItem}





        AND   msg_type  ${condition.msgType.op} #{condition.msgType.data}


        AND msg_type like CONCAT('%', #{condition.msgType.data} , '%')


        AND msg_type like CONCAT(#{condition.msgType.data} , '%')


        ${condition.groupOp}   msg_type  ${condition.msgType.op}
        #{listItem}





        AND   content_model  ${condition.contentModel.op} #{condition.contentModel.data}


        AND content_model like CONCAT('%', #{condition.contentModel.data} , '%')


        AND content_model like CONCAT(#{condition.contentModel.data} , '%')


        ${condition.groupOp}   content_model  ${condition.contentModel.op}
        #{listItem}





        AND   uniquekey  ${condition.uniquekey.op} #{condition.uniquekey.data}


        AND uniquekey like CONCAT('%', #{condition.uniquekey.data} , '%')


        AND uniquekey like CONCAT(#{condition.uniquekey.data} , '%')


        ${condition.groupOp}   uniquekey  ${condition.uniquekey.op}
        #{listItem}





ORDER BY

    ${sort.field} ${sort.order}

LIMIT 0,1

